        prevArrow={<PreviousBtn />}
        nextArrow={<NextBtn />}
        slidesToShow={3}
        slidesToScroll={1}
        infinite={false}
      >
        {multiData.map((item, index) => (
          <Card item={item}  key={index.toString()}/>
