import React from 'react'
import Slider from "react-slick";
import './LazyLoadImage.css'
import { ArrowBackIos, ArrowForwardIos } from "@material-ui/icons";


const PreviousBtn = (props) => {
    console.log(props);
    const { className, onClick } = props;
    return (
      <div className={className} onClick={onClick}>
        <ArrowBackIos style={{ color: "blue", fontSize: "30px" }} />
      </div>
    );
  };
  const NextBtn = (props) => {
    const { className, onClick } = props;
    return (
      <div className={className} onClick={onClick}>
        <ArrowForwardIos style={{ color: "blue", fontSize: "30px" }} />
      </div>
    );
  };

  const carouselProperties = {
    prevArrow: <PreviousBtn />,
    nextArrow: <NextBtn />,
    slidesToShow: 3,
    // infinite={false}
    // slidesToScroll={3}
    centerMode: true,
    centerPadding: "170px",
    responsive: [
      {
        breakpoint: 426,
        settings: {
          slidesToShow: 1,
          centerMode: false,
        },
      },
      {
        breakpoint: 769,
        settings: {
          slidesToShow: 3,
          centerMode: false,
        },
      },
      {
        breakpoint: 1025,
        settings: {
          slidesToShow: 4,
          centerMode: false,
          slidesToScroll: 2,
        },
      },
    ],
  };




const LazyLoadImage = (props) => {



    const fadeImages = [
        "images/Slide1.jpeg",
        "images/Slide2.jpeg",
        "images/Slide3.jpeg"
    ];


    // const settings = {
    //     dots: true,
    //     focusOnSelect: true,
    //     infinite: true,
    //     slidesToShow: 3,
    //     slidesToScroll: 1,
    //     speed: 500,
    //     nextArrow: <NextBtn />,
    //     prevArrow: <PreviousBtn />
    //   };

  return (
    <>
    <div style={{ margin: "30px" }} className="carousel">
      <h1>Basic carousel</h1>
      <Slider {...carouselProperties}>
        {fadeImages.map((item) => (
          <Card item={item} />
        ))}
      </Slider>
    </div>
    </>
  )
}
const Card = ({ item }) => {
    return (
      <div style={{ textAlign: "center" }}>
        <img
          className="multi__image"
          src={item}
          alt=""
          style={{
            width: "100%",
            height: "170px",
            objectFit: "contain",
            marginBottom: "10px",
          }}
        />
        <p style={{ fontSize: "14px", padding: "5px 0" }}>TOP TRNDING TVs</p>
        <p style={{ fontSize: "16px", padding: "5px 0", color: "green" }}>
          From ₹ 7,000
        </p>
        <p style={{ fontSize: "14px", padding: "5px 0", color: "gray" }}>
          Up To ₹ 5,000 Off on HDFC
        </p>
      </div>
    );
  };

export default LazyLoadImage
