import React from 'react'
import Slider from "react-slick";
import './LazyLoadImage.css'

const LazyLoadImage = (props) => {

    function SampleNextArrow(props) {
        const { className, style, onClick } = props;
        return (
          <div
            className={className}
            style={{ ...style, display: "block", background: "red" }}
            onClick={onClick}
          />
        );
      }

      function SamplePrevArrow(props) {
        const { className, style, onClick } = props;
        return (
          <div
            className={className}
            style={{ ...style, display: "block", background: "green" }}
            onClick={onClick}
          />
        );
      }


    const fadeImages = [
        "images/Slide1.jpeg",
        "images/Slide2.jpeg",
        "images/Slide3.jpeg"
    ];


    const settings = {
        dots: true,
        focusOnSelect: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        speed: 500,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />
      };

  return (
    <>
    <br/>
    <br/>
             <Slider {...settings}>
            <div >
            <img src={ fadeImages[0] } alt='...' className='img-lazy'/>
            </div>
            <div className='mgn'>
            <img src={ fadeImages[1] } alt='...' className='img-lazy' />
            </div>
            <div>
            <img src={ fadeImages[2] } alt='...' className='img-lazy' />
            </div>

          </Slider>
    </>
  )
}

export default LazyLoadImage
