import React from 'react';
import './DropDown.css';
import CloseSharpIcon from '@material-ui/icons/CloseSharp';
import MenuSharpIcon from '@material-ui/icons/MenuSharp';
import ArrowForwardIosSharpIcon from '@material-ui/icons/ArrowForwardIosSharp';

const DropDown = () => {

  /* When the user clicks on the button, 
toggle between hiding and showing the dropdown content */
  function myFunction() {
    document.getElementById("myDropdown").classList.toggle("show");
  }

  // Close the dropdown if the user clicks outside of it
  window.onclick = function (event) {
    if (!event.target.matches('.dropbtn')) {
      var dropdowns = document.getElementsByClassName("dropdown-content");
      var i;
      for (i = 0; i < dropdowns.length; i++) {
        var openDropdown = dropdowns[i];
        if (openDropdown.classList.contains('show')) {
          openDropdown.classList.remove('show');
        }
      }
    }
  }
  return (
    <div>
      <div className="dropdown">

        <MenuSharpIcon onClick={myFunction} className="dropbtn " />

        <div id="myDropdown" className="dropdown-content">
          <ul className='m_d'>
            <li className='hoverLi'>
              <a href="/">Television & Accessories 
              <ArrowForwardIosSharpIcon style={{fontSize:'20',
               color:'white',marginBottom:'-9px', 
               marginLeft:'25px'}}/></a>
              <ul className='showContent content-list'>
                <li><a href="">LED TVs</a></li>
                <li><a href="">QLED TVs</a></li>
                <li><a href="">OLED TVs</a></li>
                <li><a href="">4KLED TVs</a></li>
                <li><a href="">FULL HD TVs</a></li>
                <li><a href="">SMART TVs</a></li>
                <li><a href="">LED TVs</a></li>
                <li><a href="">QLED TVs</a></li>
                <li><a href="">OLED TVs</a></li>
              </ul>
            </li></ul>
            <ul className='m_d'>
            <li className='hoverLi'>
              <a href="/">Home Applience
              <ArrowForwardIosSharpIcon style={{fontSize:'20',
               color:'white',marginBottom:'-9px', 
               marginLeft:'78px'}}/></a>
              <ul className='showContent'>
                <li><a href="">Submenu - 4</a></li>
                <li><a href="">Submenu - 5</a></li>
                <li><a href="">Submenu - 6</a></li>
              </ul>
            </li></ul>
          <a href="/about">About</a>
          <a href="/contact">Contact</a>
          <ul className='m_d'>
            <li className='hoverLi'>
              <a href="/">Dropdown</a>
              <ul className='showContent'>
                <li><a href="">Submenu - 7</a></li>
                <li><a href="">Submenu - 8</a></li>
                <li><a href="">Submenu - \9</a></li>
              </ul>
            </li></ul>

        </div>
      </div>
    </div>
  )
}

export default DropDown
