import React, { useState } from 'react';
import './MenuList.css'
import CloseSharpIcon from '@material-ui/icons/CloseSharp';
import MenuSharpIcon from '@material-ui/icons/MenuSharp';
import IconButton from '@material-ui/core/IconButton';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import MoreVertIcon from '@material-ui/icons/MoreVert';

const options = [
  'None',
  'Atria',
  'Callisto',
  'Dione',
  'Ganymede',
  'Hangouts Call',
  'Luna',
  'Oberon',
  'Phobos',
  'Pyxis',
  'Sedna',
  'Titania',
  'Triton',
  'Umbriel',
];
const ITEM_HEIGHT = 48;


const MenuList = ({setShowMenu, showMenu}) => {

    const [anchorEl, setAnchorEl] = useState(null);
    const [showList , setShowList] = useState(true)
    const open = Boolean(anchorEl);

    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    //   setShowMenu(!showMenu);
      setShowList(!showList);
    };

    const handlelose = () => {
      setAnchorEl(null);
    };
    const handleClose =() =>{
        // setShowMenu(true)
        setShowList(showList);
    }

  return (
      <>  
     {
         showList ?

    <MenuSharpIcon className='menu_icon_first' onClick={ handleClick } />:

    <CloseSharpIcon className='menu_icon_first' onClick={handleClose}/>
     }
  <div>
      <IconButton
        aria-label="more"
        aria-controls="long-menu"
        aria-haspopup="true"
        onClick={handleClick}
        color={'white'}
      >
        <MoreVertIcon />
      </IconButton>
      <Menu
        id="long-menu"
        anchorEl={anchorEl}
        keepMounted
        open={open}
        onClose={handlelose}
        PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: '20ch',
          },
        }}
      >
        {options.map((option) => (
          <MenuItem key={option} selected={option === 'Pyxis'} onClick={handlelose}>
            {option}
          </MenuItem>
        ))}
      </Menu>
    </div>
      </>
  )
}

export default MenuList;
