import React from 'react';
import './SmallSlide.css';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { ArrowBackIos, ArrowForwardIos } from "@material-ui/icons";
import { topSlide } from '../../data';

const PreviousBtn = (props) => {
    const { className, onClick } = props;
    return (
      <div className={className} onClick={onClick}>
        <ArrowBackIos style={{ color: "gray", fontSize: "20px" }} />
      </div>
    );
  };
  const NextBtn = (props) => {
    const { className, onClick } = props;
    return (
      <div className={className} onClick={onClick}>
        <ArrowForwardIos style={{ color: "gray", fontSize: "20px" }} />
      </div>
    );
  };

const SmallSlider = () => {
  return (
    <div
    style={{ display: "flex", justifyContent: "center", marginTop: "1px" }}
  >
    <div style={{ width: "80%" }}>
      <Slider
        prevArrow={<PreviousBtn />}
        nextArrow={<NextBtn />}
        slidesToShow={8}
        slidesToScroll={1}
        infinite={false}
      >
        {topSlide.map(({image, name}, index) => (
            <div className='small-slider' key={index.toString()}>
          <img src={image} alt={name} />
          <p style={{color:'white'}}>{name}</p>
          </div>
        ))}
      </Slider>
    </div>
  </div>
  )
}

export default SmallSlider;
