import React from 'react'
import Slider from "react-slick";
import './LazyLoadImage.css'


const PreviousBtn = (props) => {
    // console.log(props);
    const { className, onClick } = props;
    return (
      <div className={className} onClick={onClick}>
        <ArrowBackIos style={{ color: "blue", fontSize: "30px" }} />
      </div>
    );
  };
  const NextBtn = (props) => {
    const { className, onClick } = props;
    return (
      <div className={className} onClick={onClick}>
        <ArrowForwardIos style={{ color: "blue", fontSize: "30px" }} />
      </div>
    );
  };




const LazyLoadImage = (props) => {



    const fadeImages = [
        "images/Slide1.jpeg",
        "images/Slide2.jpeg",
        "images/Slide3.jpeg"
    ];


    const settings = {
        dots: true,
        focusOnSelect: true,
        infinite: true,
        slidesToShow: 3,
        slidesToScroll: 1,
        speed: 500,
        nextArrow: <NextBtn />,
        prevArrow: <PreviousBtn />
      };

  return (
    <>
    <br/>
    <br/>
             <Slider {...settings}>
            <div >
            <img src={ fadeImages[0] } alt='...' className='img-lazy'/>
            </div>
            <div className='mgn'>
            <img src={ fadeImages[1] } alt='...' className='img-lazy' />
            </div>
            <div>
            <img src={ fadeImages[2] } alt='...' className='img-lazy' />
            </div>

          </Slider>
    </>
  )
}

export default LazyLoadImage
